// Route waypoints (Mediterranean -> Florida -> Mediterranean)
const route = [
    { lat: 36.4072, lon: -5.1600, name: "Gibraltar" },      // Start/End at Gibraltar
    { lat: 28.4698, lon: -16.2549, name: "Canary Islands" }, // Common Atlantic crossing point
    { lat: 25.7617, lon: -80.1918, name: "Miami" },         // Florida destination
    { lat: 24.5557, lon: -81.7826, name: "Key West" },      // Florida Keys
    { lat: 25.7617, lon: -80.1918, name: "Miami" },         // Start return journey
    { lat: 38.6971, lon: -27.2276, name: "Azores" },        // Return via Azores
    { lat: 36.4072, lon: -5.1600, name: "Gibraltar" }       // Back to start
];

// Initialize or update state
let state = context.get('boatState');
if (!state) {
    // First time - create new state and check for existing GPS position
    const globalContext = context.global;
    const currentLat = globalContext.get('victronenergy.gps._100.Position.Latitude');
    const currentLon = globalContext.get('victronenergy.gps._100.Position.Longitude');
    const currentCourse = globalContext.get('victronenergy.gps._100.Course');

    let initialSegment = 0;
    let initialProgress = 0;

    // If we have a current position, find closest point on route
    if (currentLat && currentLon) {
        let minDistance = Infinity;
        let bestSegment = 0;
        let bestProgress = 0;

        // Check each route segment
        for (let i = 0; i < route.length - 1; i++) {
            const start = route[i];
            const end = route[i + 1];

            // Find closest point on this segment
            for (let progress = 0; progress <= 1; progress += 0.01) {
                const testPos = interpolatePosition(start, end, progress);
                const distance = calculateDistance(currentLat, currentLon, testPos.lat, testPos.lon);

                if (distance < minDistance) {
                    minDistance = distance;
                    bestSegment = i;
                    bestProgress = progress;
                }
            }
        }

        // Only use the found position if it's reasonably close (within 50km)
        if (minDistance < 50) {
            initialSegment = bestSegment;
            initialProgress = bestProgress;
            node.warn(`Resuming trip from segment ${bestSegment} at ${Math.round(bestProgress * 100)}% progress (${Math.round(minDistance)}km from route)`);
        } else {
            node.warn(`Current position too far from route (${Math.round(minDistance)}km), starting from Gibraltar`);
        }
    } else {
        node.warn('No current GPS position found, starting from Gibraltar');
    }

    state = {
        currentSegment: initialSegment,
        progress: initialProgress,
        direction: 1,
        startTime: new Date().getTime(),
        satellites: 12
    };
}

// Function to calculate distance between two points using Haversine formula
function calculateDistance(lat1, lon1, lat2, lon2) {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
        Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
        Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
}

// Function to interpolate between two points
function interpolatePosition(start, end, progress) {
    return {
        lat: start.lat + (end.lat - start.lat) * progress,
        lon: start.lon + (end.lon - start.lon) * progress
    };
}

// Update position
const timeElapsed = (new Date().getTime() - state.startTime) / 1000; // seconds
const averageSpeed = 8; // km/h (realistic cruising speed)
const distancePerSecond = averageSpeed / 3600; // km/second

// Add some speed variation (±20%)
const speedVariation = 0.8 + (Math.random() * 0.4);
const adjustedDistance = distancePerSecond * speedVariation * timeElapsed;

// Get current segment points
const startPoint = route[state.currentSegment];
const endPoint = route[(state.currentSegment + 1) % route.length];

// Calculate segment length
const segmentDistance = calculateDistance(startPoint.lat, startPoint.lon, endPoint.lat, endPoint.lon);

// Update progress
state.progress += adjustedDistance / segmentDistance;

// Update satellite count occasionally (every ~5 minutes)
if (Math.random() < 0.01) {  // 1% chance each update
    // Base satellite count 8-14
    let newSatCount = 8 + Math.floor(Math.random() * 7);

    // Occasional poor reception (5% chance)
    if (Math.random() < 0.05) {
        newSatCount = 3 + Math.floor(Math.random() * 4); // 3-6 satellites
    }

    state.satellites = newSatCount;
}

// Check if we've completed current segment
if (state.progress >= 1) {
    state.currentSegment = (state.currentSegment + 1) % route.length;
    state.progress = 0;
    state.startTime = new Date().getTime();
}

// Calculate current position
const currentPosition = interpolatePosition(startPoint, endPoint, state.progress);

// Add small random variations to simulate GPS jitter (±0.0001 degrees)
const jitter = 0.0001;
currentPosition.lat += (Math.random() - 0.5) * jitter;
currentPosition.lon += (Math.random() - 0.5) * jitter;

// Calculate heading based on current segment
const heading = Math.atan2(
    endPoint.lon - startPoint.lon,
    endPoint.lat - startPoint.lat
) * 180 / Math.PI;

// Calculate speed in knots (with variation)
const speedKnots = averageSpeed * speedVariation * 0.539957; // Convert km/h to knots

// Save state
context.set('boatState', state);

// Update node status
const progressPercent = Math.round(state.progress * 100);
node.status({
    fill: "blue",
    shape: "dot",
    text: `${startPoint.name} → ${endPoint.name} (${progressPercent}%)`
});

// Output GPS data as JSON object with dbus paths
msg.payload = {
    "Position/Longitude": Math.round(currentPosition.lon * 1000000) / 1000000,
    "Position/Latitude": Math.round(currentPosition.lat * 1000000) / 1000000,
    "Speed": Math.round(speedKnots * 100) / 100,
    "Course": Math.round(heading < 0 ? heading + 360 : heading),
    "Altitude": 0,
    "NrOfSatellites": state.satellites,
    "Fix": 1
};

return msg;
